import React, { useState, useEffect, useRef, useCallback } from 'react';
import axios from "axios";
import './App.css';

import "bootstrap/dist/css/bootstrap.min.css";
import {Card, Row, Col} from 'react-bootstrap'
import { useIntersectionObserver } from "../../hooks/useIntersectionObserver";

const limit = 9;

export default function DataCollect(){

            const [data, setData] = useState();
            const [isError, setIsError] = useState(false);
            const [isInitialFetch, setIsInitialFetch] = useState(false);

            const pageRef = useRef(1);
            const [hasMore, setHasMore] = useState(false);

            const fetchPosts = async ({ page, isFetchingFirstTime }) => {
                try {
                  setIsError(false);
                  isFetchingFirstTime && setIsInitialFetch(true);

                  const resPro = await fetch(
                    `http://localhost:3000/products?limit=${limit}&skip=${
                      (page - 1) * limit
                    }`
                  );

            const data = await resPro.json();

            if (data.products) {
                    setData((prev) => {
                       const newData = [...prev, ...data.products];

                      if (newData.length < data.total) {
                        setHasMore(true);
                      } else setHasMore(false);

                      return newData;
                    });
                  } else {
                    setHasMore(false);
                  }
                } catch (err) {
                  setHasMore(false);
                  setIsError(true);
                } finally {
                  setIsInitialFetch(false);
                }
            };


            useEffect(() => {
                fetchPosts({ page: pageRef.current, isFetchingFirstTime: true });
            }, [])

            return (
                <div className="App">
                    {
                        <div className='products'>
                            {
                            data?.map((data) => {
                                return (
                                    <div key={data.id}>
                                        <img className='img'
                                            src={data.image}
                                            alt="img" />
                                        <h1>{data.name}</h1>
                                        <p>{data.description}</p>
                                    </div>
                                );
                            })
                            }
                        </div>
                    }
                </div>
            );
        }
