import React, { useState, useEffect, useRef, useCallback } from 'react';
import axios from "axios";
import './App.css';

export default function DataCollect(){

            const [data, setData] = useState();


            useEffect(() => {
                fetchPosts({ page: pageRef.current, isFetchingFirstTime: true });
            }, [])

            // Infinite scrolling
              const [targetRef, isIntersecting] = useIntersectionObserver({ threshold: 1 });

              useEffect(() => {
                if (hasMore && isIntersecting) {
                  pageRef.current = pageRef.current + 1;
                  fetchPosts({ page: pageRef.current });
                }
              }, [isIntersecting, hasMore]);

            return (
                <div className="App">
                    {
                        <div className='products'>
                            {
                            data?.map((data) => {
                                return (
                                    <div key={data.id}>
                                        <img className='img'
                                            src={data.image}
                                            alt="img" />
                                        <h1>{data.name}</h1>
                                        <p>{data.description}</p>
                                    </div>
                                );
                            })
                            }
                        </div>
                    }
                    {hasMore && (
                            <div ref={targetRef}>
                              <Spinner text="Loading more products..." />
                            </div>
                          )}
                </div>
            );
        }
