import React, { useState, useEffect } from 'react';
import axios from "axios";
import './App.css';
import InfiniteScroll from 'react-infinite-scroll-component'
import './style.css'

export default function DataCollection() {
    const [data, setData] = useState();

        useEffect(() => {
            const fetchData = async () => {
                try {
                    const res = await fetch(`http://localhost:3000/products?offset=0&_limit=4`)
                    const data = await res.json()
                    setData(data)

                } catch(error) {
                    console.log(error)
                }


            }
        fetchData()
        }, [])

        return (
            <div className="App">
                {
                    <div className='products'>
                        {data?.map((data) => {
                            return (
                                <div key={data.id}>
                                    <img className='img'
                                        src={data.image}
                                        alt="img" />
                                    <h1>{data.name}</h1>
                                    <p>{data.description}</p>
                                </div>
                            );
                        })
                        }
                    </div>
                }
            </div>
        );
}
